t 0 0 5px grey;\n	border-radius: 10px;\n}\n/* Handle */\n::-webkit-scrollbar-thumb {\n	background: #888;\n	border-radius: 10px;\n}\n/* Handle on hover */\n::-webkit-scrollbar-thumb:hover {\n	background: #555;\n}\n\n\n/* --------------------------- \n	Card Columns\n	--------------------------- */\n.card-columns .card {\n	margin-bottom: .75rem\n}\n.card-columns {\n	-webkit-column-gap: 1.25rem;\n	-moz-column-gap: 1.25rem;\n	column-gap: 1.25rem;\n	orphans: 1;\n	widows: 1\n}\n.card-columns .card {\n	display: inline-block;\n	width: 100%;\n}\n/* sm */\n@media ( min-width:576px) {\n	.card-columns {\n		-webkit-column-count: 1;\n		-moz-column-count: 1;\n		column-count: 1;\n	}\n}\n/* md */\n@media ( min-width:768px) {\n	.card-columns {\n		-webkit-column-count: 2;\n		-moz-column-count: 2;\n		column-count: 2;\n	}\n}\n/* lg */\n@media ( min-width:992px) {\n	.card-columns {\n		-webkit-column-count: 3;\n		-moz-column-count: 3;\n		column-count: 3;\n	}\n}\n/* xl */\n@media ( min-width:1300px) {\n	.card-columns {\n		-webkit-column-count: 4;\n		-moz-column-count: 4;\n		column-count: 4;\n	}\n}\n\n'),
	(43, 32, 'exp_templates', 'template_data', 1637445703, 1, '{layout=\"layouts/_html-wrapper\"}\n\n{!-- \n    include_assets=\"no\" \n    include_css=\"no\" \n --}\n\n\n{exp:channel:form\n    channel=\"post\"\n    entry_id=\"1\"\n    show_fields=\"art_img|art_date|art_desc\"\n    return=\"home/test\"\n    id=\"chanelEntryForm\"\n    name=\"entryForm\"\n    class=\"some-class\"\n    author_only=\"no\"\n    include_assets=\"no\"\n    include_css=\"no\"\n    include_jquery=\"no\"\n    secure_action=\"no\"\n    secure_return=\"no\"\n    site=\"art_hub\"\n    }\n\n<div class=\"card shadow\">\n    <nav class=\"card-header\">\n        <div class=\"nav nav-tabs card-header-tabs\" id=\"nav-tab\" role=\"tablist\">\n            {!-- main tab button --}\n            <button class=\"nav-link active\" id=\"nav-general-tab\" data-bs-toggle=\"tab\" data-bs-target=\"#nav-general\" type=\"button\" role=\"tab\" aria-controls=\"nav-general\" aria-selected=\"true\">General</button>\n            {!-- categories tab button --}\n            <button class=\"nav-link\" id=\"nav-categories-tab\" data-bs-toggle=\"tab\" data-bs-target=\"#nav-categories\" type=\"button\" role=\"tab\" aria-controls=\"nav-categories\" aria-selected=\"true\">Categories</button>\n            {!-- settings tab button --}\n            <button class=\"nav-link\" id=\"nav-settings-tab\" data-bs-toggle=\"tab\" data-bs-target=\"#nav-settings\" type=\"button\" role=\"tab\" aria-controls=\"nav-settings\" aria-selected=\"false\">Settings</button>\n        </div>\n    </nav>\n    <div class=\"card-body\">\n        \n        <div class=\"tab-content\" id=\"nav-tabContent\">\n            \n            {!-- main tab --}\n            <div class=\"tab-pane fade show active\" id=\"nav-general\" role=\"tabpanel\" aria-labelledby=\"nav-general-tab\">\n                \n                {!-- Title --}\n                <fieldset class=\"fieldset-required\">\n                    <div class=\"field-instruct\">\n                        <label for=\"title\">Title</label>\n                    </div>\n                    <div class=\"fieldset-control\">\n                        <input type=\"text\" name=\"title\" id=\"title\" value=\"{title}\" maxlength=\"100\" required>\n                    </div>\n                </fieldset>\n\n                {!-- URL Title --}\n                <fieldset class=\"fieldset-required\">\n                    <div class=\"field-instruct\">\n                        <label for=\"url_title\">URL Title</label>\n                        <em id=\"urlTitleHelp\">No spaces. Underscores and dashes are allowed.</em>\n                    </div>\n                    <div class=\"fieldset-control\">\n                        <input type=\"text\" name=\"url_title\" id=\"url_title\" value=\"{url_title}\" maxlength=\"75\" aria-describedby=\"urlTitleHelp\" required>\n                    </div>\n                </fieldset>\n\n\n                {!-- Art Image --}\n                <fieldset class=\"fieldset-required\">\n                    <div class=\"field-instruct\">\n                        <label for=\"art_img\">{label:art_img}</label>\n                        <em id=\"artImageHelp\">{instructions:art_img}</em>\n                    </div>\n                    <div class=\"fieldset-control\">\n                        {!-- <input type=\"text\" name=\"art_img\" id=\"art_img\" data-value=\"{art_img}\" value=\"{art_img}\" aria-describedby=\"artImageHelp\" required> --}\n                        {exp:art_hub:file entry_id=\"{entry_id}\" field_name=\"art_img\" value=\"{art_img}\"}\n                    </div>\n                </fieldset>\n                \n\n\n                {!-- Art Date --}\n                <fieldset class=\"fieldset-required\">\n                    <div class=\"field-instruct\">\n                        <label for=\"art_date\">{label:art_date}</label>\n                        <em id=\"artDateHelp\">{instructions:art_date}</em>\n                    </div>\n                    <div class=\"fieldset-control\">\n                        <input type=\"text\" name=\"art_date\" id=\"art_date\" data-value=\"{art_date}\" value=\"{art_date format=\'%n/%j/%Y %g:%i %A\'}\" aria-describedby=\"artDateHelp\" required>\n                    </div>\n                </fieldset>\n\n                {!-- Art Description --}\n                <fieldset class=\"fieldset-required\">\n                    <div class=\"field-instruct\">\n                        <label for=\"art_desc\">{label:art_desc}</label>\n                        <em id=\"artDescriptionHelp\">{instructions:art_desc}</em>\n                    </div>\n                    <div class=\"fieldset-control\">\n                        <textarea id=\"art_desc\" name=\"art_desc\" rows=\"4\" aria-describedby=\"artDescriptionHelp\" required>{art_desc}</textarea>\n                    </div>\n                </fieldset>\n\n            </div>\n\n            {!-- categories tab --}\n            <div class=\"tab-pane fade\" id=\"nav-categories\" role=\"tabpanel\" aria-labelledby=\"nav-categories-tab\">\n                {!-- Categories --}\n                <div class=\"mb-3\">\n                    {category_menu}\n                    <label for=\"category_{entry_id}\">Categories</label>\n                    <select name=\"category[]\" id=\"category_{entry_id}\" class=\"form-select\" multiple=\"multiple\" aria-describedby=\"categoriesHelp\">\n                        {select_options}\n                    </select>\n                    {/category_menu}\n                    <div id=\"categoriesHelp\" class=\"form-text\">Categorize this entry.</div>\n                </div>\n            </div>\n\n            {!-- settings tab --}\n            <div class=\"tab-pane fade\" id=\"nav-settings\" role=\"tabpanel\" aria-labelledby=\"nav-settings-tab\">\n                {!-- Date --}\n                {!-- <input type=\"text\" name=\"field_id_2\" value=\"8/9/2021 9:06 PM\" rel=\"date-picker\" data-date-format=\"%n/%j/%Y %g:%i %A\" data-timestamp=\"1628564760\"> --}\n                <div class=\"mb-3\">\n                    <label for=\"entry_date_{entry_id}\" class=\"form-label\">Date</label>\n                    <input type=\"text\" name=\"entry_date\" id=\"entry_date_{entry_id}\" class=\"form-control\" value=\"{entry_date}\" maxlength=\"23\"  rel=\"date-picker\" data-date-format=\"%n/%j/%Y %g:%i %A\" aria-describedby=\"entryDateHelp\" required>\n                    <div id=\"entryDateHelp\" class=\"form-text\">Date of publication for this entry.</div>\n                </div>\n\n                {!-- Status --}\n                <div class=\"mb-3\">\n                    {status_menu}\n                    <label for=\"status_{entry_id}\">Status</label>\n                    <select class=\"form-select\" name=\"status\" id=\"status_{entry_id}\">\n                        {select_options}\n                    </select>\n                    {/status_menu}\n                </div>\n                \n                {!-- Expiration Date --}\n                <div class=\"mb-3\">\n                    <label for=\"expiration_date_{entry_id}\" class=\"form-label\">Expiration Date</label>\n                    <input type=\"text\" name=\"expiration_date\" id=\"expiration_date_{entry_id}\" class=\"form-control\" value=\"{expiration_date}\" maxlength=\"23\" aria-describedby=\"expirationDateHelp\">\n                    <div id=\"expirationDateHelp\" class=\"form-text\">Date this entry should expire.</div>\n                </div>\n                \n                {!-- Comment Expiration Date --}\n                <div class=\"mb-3\">\n                    <label for=\"comment_expiration_date_{entry_id}\" class=\"form-label\">Comment Expiration Date</label>\n                    <input type=\"text\" name=\"comment_expiration_date\" id=\"comment_expiration_date_{entry_id}\" class=\"form-control\" value=\"{comment_expiration_date}\" maxlength=\"23\" aria-describedby=\"commentExpirationDateHelp\">\n                    <div id=\"commentExpirationDateHelp\" class=\"form-text\">Date comments for this entry should no longer be accepted.</div>\n                </div>\n                \n                {!-- Make Entry Sticky --}\n                <div class=\"form-check mb-3\">\n                    <input type=\"checkbox\" name=\"sticky\" id=\"sticky_{entry_id}\" class=\"form-check-input\" value=\"y\" {sticky}>\n                    <label class=\"form-check-label\" for=\"sticky_{entry_id}\">Make Entry Sticky</label>\n                </div>\n                \n                {!-- Allow Comments --}\n                <div class=\"form-check mb-3\">\n                    <input type=\"checkbox\" name=\"allow_comments\" id=\"allow_comments_{entry_id}\" class=\"form-check-input\" value=\"y\" {allow_comments}>\n                    <label class=\"form-check-label\" for=\"allow_comments_{entry_id}\">Allow Comments</label>\n                </div>\n                \n                \n            </div>\n        </div>\n\n        {!-- Captcha --}\n        {if captcha}\n        <div class=\"mb-3\">\n            <label for=\"captcha_{entry_id}\">Please enter the word you see in the image below:</label>\n            {captcha}\n            <input type=\"text\" name=\"captcha\" id=\"captcha_{entry_id}\" value=\"{captcha_word}\" maxlength=\"20\">\n        </div>\n        {/if}\n        \n        {!-- Submit Button --}\n        <button type=\"submit\" class=\"btn btn-primary\">Submit</button>\n        \n    </div>\n</div>\n\n\n{/exp:channel:form}\n'),
	(44, 8, 'exp_templates', 'template_data', 1637446275, 1, '<!doctype html>\n<html dir=\"ltr\" lang=\"{lang}\">\n    <head>\n        <!-- Required meta tags -->\n        <meta charset=\"utf-8\">\n        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n        \n        <!-- title -->\n        <title>{if layout:title}{layout:title} - {/if}{site_name}</title>\n        \n        <!-- icon (shown in tab) -->\n        <link rel=\"icon\" href=\"{site_url}/icon.jpg\">\n        \n        <!-- meta data -->\n        {if layout:meta_author}<meta name=\"author\" content=\"{layout:meta_author}\">{/if}\n        {if layout:meta_description}<meta name=\"description\" content=\"{layout:meta_description}\">{/if}\n\n        <!-- open graph common -->\n        <meta property=\"og:site_name\" content=\"{site_name}\">\n        <meta property=\"og:type\" content=\"website\">\n        {!-- og:image? let\'s figure this out --}\n        {!-- square, 50*50 min --}\n        {!-- <meta property=\"og:image\" content=\"{theme_user_folder_url}site/default/asset/img/og/default.jpg\"> --}\n\n        {if layout:og_title != \'\'}\n            <!-- open graph per page -->\n            <meta property=\"og:title\" content=\"{layout:og_title}\">\n            {if layout:og_url != \'\'}<meta property=\"og:url\" content=\"{layout:og_url}\">{/if}\n            {if layout:og:description != \'\'}<meta property=\"og:description\" content=\"{layout:og_description}\">{/if}\n        {/if}\n        \n        <!-- CSS -->\n        <link href=\"{theme_user_folder_url}bootstrap/css/bootstrap.min.css\" rel=\"stylesheet\">\n        <link href=\"{theme_user_folder_url}fontawesome/css/all.min.css\" rel=\"stylesheet\">\n        <link href=\"{stylesheet=\'common/_global-css\'}\" rel=\"stylesheet\">\n    </head>\n      <body data-ng-app=\"myApp\">\n        <header>\n            {snp_header_navbar}\n        </header>\n        <div id=\"mainPageBody\">\n            {snp_breadcrumbs}\n            {if layout:title}\n            <div id=\"pageTitleContainer\" class=\"container\">\n                {if layout:title}<h1 id=\"pageTitle\">{layout:title}</h1>{/if}\n            </div>\n            {/if}\n            <div class=\"container\">\n                {layout:contents}\n            </div>\n        </div>\n        <footer>\n            {snp_footer_navbar}\n        </footer>\n        <!-- JavaScript -->\n        <script src=\"{theme_user_folder_url}jquery/jquery-3.6.0.min.js\"></script>\n        {exp:art_hub:script_foot}\n        <script src=\"https://ajax.googleapis.com/ajax/libs/angularjs/1.6.9/angular.min.js\"></script>\n        <script src=\"{theme_user_folder_url}assets/js/angular-app.js\"></script>\n        <script src=\"{theme_user_folder_url}bootstrap/js/bootstrap.bundle.min.js\"></script>\n        <script src=\"{theme_user_folder_url}fontawesome/js/all.min.js\"></script>\n        <script src=\"{theme_user_folder_url}assets/js/main.js\"></script>\n        {embed=\"common/_global-js\"}\n        {layout:scripts}\n    </body>\n</html>'),
	(45, 26, 'exp_templates', 'template_data', 1637450806, 1, '{layout=\'layouts/_artist-profile\'}\n\n{layout:set:append name=\'breadcrumbs\'}Artists{/layout:set:append}\n{layout:set:append name=\'breadcrumbs_urls\'}artist{/layout:set:append}\n\n\n{exp:member:custom_profile_data member_id=\"{segment_3}\"}\n    \n    {layout:set:append name=\'breadcrumbs\'}{username}{/layout:set:append}\n	{layout:set:append name=\'breadcrumbs_urls\'}artist/view/{member_id}{/layout:set:append}\n\n    {layout:set name=\"artist_name\"}{screen_name}{/layout:set}\n    {layout:set name=\"artist_id\"}{member_id}{/layout:set}\n    {layout:set name=\"artist_username\"}{username}{/layout:set}\n    {layout:set name=\"artist_screen_name\"}{screen_name}{/layout:set}\n    {layout:set name=\"total_entries\"}{total_entries}{/layout:set}\n\n    {layout:set name=\"title\"}{username}\'s Profile{/layout:set}\n    <div class=\"d-flex flex-column flex-md-row\">\n        <img src=\"{avatar_url}\" class=\"rounded-circle me-md-3\" height=\"100\" width=\"100\">\n        <div class=\"d-flex flex-column\">\n            <div class=\"d-flex flex-column flex-lg-row\">\n                <div class=\"d-flex flex-column me-lg-4\">\n                    {if first_name != \'\' && last_name != \'\'}\n                    <h2>{first_name} {last_name}</h2>\n                    {if:else}\n                    <h2>{screen_name}</h2>\n                    {/if}\n                    <span>@{username}</span>\n                </div>\n                <div class=\"d-flex flex-column pt-lg-5\">\n                    {if last_activity}\n                    <div>\n                        <span class=\"me-1\">Last Activity:</span>\n                        <span class=\"font-monospace\">{last_activity:relative}</span>\n                    </div>\n                    {if:elseif last_visit}\n                    <div>\n                        <span class=\"me-1\">Last Visit:</span>\n                        <span class=\"font-monospace\">{last_visit:relative}</span>\n                    </div>\n                    {if:elseif last_entry_date}\n                    <div>\n                        <span class=\"me-1\">Last Entry:</span>\n                        <span class=\"font-monospace\">{last_entry_date:relative}</span>\n                    </div>\n                    {if:else}\n                    <div>\n                        <span class=\"me-1\">Joined:</span>\n                        <span class=\"font-monospace\">{join_date:relative}</span>\n                    </div>\n                    {/if}\n                    <div>\n                        <a href=\"{path=\'artist/blog/{member_id}\'}\">\n                        	<span class=\"font-monospace\">{exp:art_hub:author_entry_count member_id=\"{member_id}\" channel=\"post\"}</span>\n                        	<span class=\"ms-1\">Posts</span>\n                        </a>\n                    </div>\n                    <div>\n                        <span class=\"font-monospace\">{total_comments}</span>\n                        <span class=\"ms-1\">Comments{if last_comment_date}<span class=\"ms-1\"> - <span class=\"font-monospace\">{last_comment_date:relative}</span>{/if}</span>\n                    </div>\n                </div>\n            </div>\n            {if profile}\n            <span class=\"fs-5 fw-bold mt-2\">Profile Bio</span>\n            <p class=\"lh-sm\">{profile}</p>\n            {/if}\n            {if member_id != logged_in_member_id}\n            	<a class=\"btn btn-primary\" href=\"{path=\'commissions/{member_id}\'}\">Request Commission</a>\n            {/if}\n        </div>\n    </div>\n{/exp:member:custom_profile_data}'),
