	(137, 8, 'exp_templates', 'template_data', 1638012746, 1, '<!doctype html>\n<html dir=\"ltr\" lang=\"{lang}\">\n    <head>\n        <!-- Required meta tags -->\n        <meta charset=\"utf-8\">\n        <meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\n        \n        <!-- title -->\n        <title>{if layout:title}{layout:title} - {/if}{site_name}</title>\n        \n        <!-- icon (shown in tab) -->\n        <link rel=\"icon\" href=\"{site_url}/icon.jpg\">\n        \n        <!-- meta data -->\n        {if layout:meta_author}<meta name=\"author\" content=\"{layout:meta_author}\">{/if}\n        {if layout:meta_description}<meta name=\"description\" content=\"{layout:meta_description}\">{/if}\n\n        <!-- open graph common -->\n        <meta property=\"og:site_name\" content=\"{site_name}\">\n        <meta property=\"og:type\" content=\"website\">\n        {!-- og:image? let\'s figure this out --}\n        {!-- square, 50*50 min --}\n        {!-- <meta property=\"og:image\" content=\"{theme_user_folder_url}site/default/asset/img/og/default.jpg\"> --}\n\n        {if layout:og_title != \'\'}\n            <!-- open graph per page -->\n            <meta property=\"og:title\" content=\"{layout:og_title}\">\n            {if layout:og_url != \'\'}<meta property=\"og:url\" content=\"{layout:og_url}\">{/if}\n            {if layout:og:description != \'\'}<meta property=\"og:description\" content=\"{layout:og_description}\">{/if}\n        {/if}\n        \n        <!-- CSS -->\n        <link href=\"{theme_user_folder_url}assets/css/default.min.css\" rel=\"stylesheet\">\n        <link href=\"{theme_user_folder_url}fontawesome/css/all.min.css\" rel=\"stylesheet\">\n        <link href=\"{stylesheet=\'common/_global-css\'}\" rel=\"stylesheet\">\n        <script src=\"{theme_user_folder_url}jquery/jquery-3.6.0.min.js\"></script>\n    </head>\n      <body data-ng-app=\"myApp\">\n        <header>\n            {snp_header_navbar}\n        </header>\n    