	(138, 12, 'exp_templates', 'template_data', 1638012766, 1, '{layout=\'layouts/_html-wrapper\'}\n\n{layout:set:prepend name=\'breadcrumbs\'}Posts{/layout:set:prepend}\n{layout:set:prepend name=\'breadcrumbs_urls\'}post{/layout:set:prepend}\n\n{layout:contents}\n\n{if layout:allow_comments == \"yes\"}\n\n	<div class=\"card\" style=\"background-color: #24314d; color: white; margin: 20px 0px; padding: 10px\">\n		<h3 class=\"car-title\">Comments...</h3>\n		<div class=\"card-deck\" style=\"color: black\">\n			{exp:comment:form channel=\"post\" return=\"\"}\n			<div class=\"card\" style=\"margin: 10px 0px; padding: 10px;\">\n				<h3 class=\"card-title\">Add a Comment...</h3>\n			{if logged_out}\n				<label for=\"name\">Name:</label> <input type=\"text\" name=\"name\" value=\"{name}\" size=\"50\" /><br />\n				<label for=\"email\">Email:</label> <input type=\"text\" name=\"email\" value=\"{email}\" size=\"50\" /><br />\n				<label for=\"location\">Location:</label> <input type=\"text\" name=\"location\" value=\"{location}\" size=\"50\" /><br />\n				<label for=\"url\">URL:</label> <input type=\"text\" name=\"url\" value=\"{url}\" size=\"50\" /><br />\n			{/if}\n			{if logged_in}\n				<h5 class=\"card-subtitle\">From: {screen_name} on {current_time format=\"%m/%d/%Y\"}</h5>\n			{/if}\n			\n			<label for=\"comment\" class=\"card-text form-label\">Comment:</label>\n			<textarea name=\"comment\" cols=\"5\" rows=\"5\" class=\"form-control\">{comment}</textarea><br>\n			<label><input type=\"checkbox\" name=\"save_info\" value=\"yes\" {save_info} /> Remember my personal information</label><br />\n			<label><input type=\"checkbox\" name=\"notify_me\" value=\"yes\" {notify_me} /> Notify me of follow-up comments?</label><br />\n			\n			<input type=\"submit\" name=\"submit\" value=\"Submit\" />\n			{!-- <input type=\"submit\" name=\"preview\" value=\"Preview\" /> --}\n			</div>\n			{/exp:comment:form}\n			{exp:comment:entries sort=\"desc\" limit=\"20\" orderby=\"date\"}\n			<div clas