	(142, 24, 'exp_templates', 'template_data', 1638012820, 1, '{layout=\'layouts/_post-layout\'}\n\n{!-- text replacements --}\n{preload_replace:ch=\"post\"}\n{preload_replace:cache_entry=\"no\"}\n{preload_replace:cache_refresh=\"60\"}\n\n{!-- set layout vars --}\n{layout:set name=\"ch\"}{ch}{/layout:set}\n\n{!--\n    CHANNEL ENTRIES TAG : START\n --}\n{exp:channel:entries channel=\"{ch}\" url_title=\"{segment_3}\" cache=\"{cache_entry}\" refresh=\"{cache_refresh}\" dynamic=\"no\" limit=\"1\" require_entry=\"yes\" track_views=\"one\"}\n\n	{!-- No Results --}\n    {if no_results} {redirect=\"home/404\"} {/if}\n	\n	{!-- Layout Vars --}\n    {layout:set name=\"title\"}{title}{/layout:set}\n    {layout:set name=\"url_title\"}{url_title}{/layout:set}\n    {layout:set name=\"author\"}{author}{/layout:set}\n    {layout:set name=\"author_id\"}{author_id}{/layout:set}\n	{layout:set name=\"author_id\"}{entry_id}{/layout:set}\n\n	{if allow_comments}\n		{layout:set name=\"allow_comments\"}yes{/layout:set}\n	{/if}\n\n	{layout:set:append name=\'breadcrumbs\'}{embed=\"common/_member-name\" member_id=\"{author_id}\"}{/layout:set:append}\n	{layout:set:append name=\'breadcrumbs_urls\'}artist/view/{author_id}{/layout:set:append}\n	\n	{layout:set:append name=\'breadcrumbs\'}Blog{/layout:set:append}\n	{layout:set:append name=\'breadcrumbs_urls\'}artist/blog/{author_id}{/layout:set:append}\n	\n	{layout:set:append name=\'breadcrumbs\'}{title}{/layout:set:append}\n	{layout:set:append name=\'breadcrumbs_urls\'}{segment_1}/{segment_2}/{segment_3}{/layout:set:append}\n	\n	<div class=\"card\">\n		<div class=\"card-body\">\n			<h3 class=\"card-title\">{title}</h3>\n			<h5 class=\"card-subtitle text-muted\">by <a href=\"{path=\'artist/view/{author_id}\'}\">{embed=\"common/_member-name\" member_id=\"{author_id}\"}</a></h5><br>\n			{if art_img}\n				<img src=\"{art_img}\" alt=\"Art Work\">\n			{/if}\n			<p class=\"card-text\">\n			{if art_date}\n				<span>Date: {art_date format=\"%F %d %Y\"}</span><