	(148, 14, 'exp_templates', 'template_data', 1638015202, 1, '/* \n--------------------------- \n	Global CSS\n--------------------------- \n*/\n\n:root {\n	--art_hub-green: #4c947e;\n	--art_hub-blue: #24314d;\n}\n\nbody {\n	/* background-color: #24314d; */\n	background-color: rgba(0,0,0,0.1);\n	/* color: #e3dbdc; */\n	/* padding to avoid content beneath fixed header navbar */\n	padding-top: 70px;\n	/* padding to avoid content beneath fixed footer navbar */\n	padding-bottom: 70px; \n}\n\n\n\n/* --------------------------- \n	Post Cards\n--------------------------- */\n/* category badges */\n.post-categories .badge {\n	text-decoration: none;\n}\n.post-categories .badge:hover {\n	color: white;\n}\n\n\n.sidebar-toc {\n	padding-left: 1.5em;\n	padding-right: 1.5em;\n	padding-top: .5em;\n	padding-bottom: .5em;\n	margin: 1em;\n	margin-top: 0px;\n	white-space: nowrap;\n	align-self: flex-start!important;\n	background-color: white;\n	border-radius: 10px;\n\n}\n#nav_categories, #nav_categories ul {\n	list-style-type: none;\n	padding: 0px;\n}\n#nav_categories ul {\n	border-left: 2px solid #e2e5ee;\n	padding-left: 1em;\n	margin-left: .5em;\n}\n\n.sidebar-toc>ul>li {\n	font-weight: 800;\n}\n.sidebar-toc>ul>li>a {\n	color: #495057;\n}\n\n\n.sidebar-toc>ul>li>ul li {\n	font-weight: 400;\n}\n.sidebar-toc>ul>li>ul li>a {\n	color: #6c757d\n}\n\n#nav_categories a {\n	text-decoration: none;\n}\n\n#nav_categories a.active {\n	color: #f62958;\n}\n\n\n\n\n\n\n\n\n/* --------------------------- \n	Navbars\n--------------------------- */\n/* header nvabar */\nheader > nav {\n	background-color: var(--art_hub-green);\n}\n/* navbar-brand & navbar links color */\nheader > nav .navbar-brand, \nheader > nav .navbar-nav > .nav-item > .nav-link {\n	color: midnightblue;\n}\n/* active link color */\nheader > nav .navbar-nav > .nav-item > .nav-link.active {\n	color: black;\n}\nheader > nav .navbar-nav > .nav-item > .nav-link {\n	border-radius: 10px;\n}\nheader > nav .navbar-nav > .nav-item:hover .nav-link {\n	color: #e3dbdc;\n	box-shadow: #24314d 1px 1px 3px;\n	background-color: seagreen;\n}\nheader > nav .navbar-toggler {\n	background-color: #4c947e;\n	box-shadow: #24314d 2px 2px 5px;\n}\nheader > nav .navbar-toggler i {\n	color: var(--art_hub-green);\n}\n\n/* footer navbar */\nfooter > nav {\n	background-color: var(--art_hub-blue);\n}\n\n/* --------------------------- \n	Navbar Positioning\n	- fixed header nav to top\n	- fix footer nav to bottom\n--------------------------- */\nfooter, header{\n	z-index:10;\n	position: fixed;\n	left: 0;\n	right: 0;\n}\nheader {\n	top: 0;\n	/* box-shadow: 0 4px 8px 0 rgba(0, 0, 0, .2), 0 6px 20px 0 rgba(0, 0, 0, .19) */\n}\nfooter {\n	bottom: 0;\n	/* box-shadow: 0 -4px 8px 0 rgba(0, 0, 0, .2), 0 -6px 20px 0 rgba(0, 0, 0, .19) */\n}\n\nimg.ee-logo{\n	height:16px;\n	padding-bottom:1px\n}\n\n\n\n/* --------------------------- \n	Clickable Cards (Site Homepage)\n	--------------------------- */\nul.category-list {\n	list-style: none;\n}\n\nul.category-list li {\n	display: inline;\n}\n\n.category-label {\n	border: 1px solid grey;\n}\n\n\n/* --------------------------- \n	Clickable Cards (Site Homepage)\n	--------------------------- */\n.card.clickable.hover {\n	/* use with light body background color\n	box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);\n	*/\n	box-shadow: 0 4px 8px 0 rgba(255, 255, 255, 0.2), 0 6px 20px 0 rgba(255, 255, 255, 0.19);\n	cursor: pointer;\n}\n.card.clickable.hover li, .card.clickable.hover .card-header{\n	background-color: #f2f2f2;\n}\n\n/* --------------------------- \n	Forms & Fields\n	--------------------------- */\ntextarea:focus, input:focus{\n	outline: none;\n	border: 1px solid black;\n	box-shadow: 0 0 3px #719ECE;\n}\n\nfieldset {\n	margin: 0px;\n	padding: 0px;\n	margin-bottom: 20px;\n	border: 0px;\n}\n\nfieldset > .field-instruct {\n	margin-bottom: 5px;\n}\n\n.fieldset-required > .field-instruct label:after {\n	content: \"*\";\n	color: var(--bs-danger);\n	font-size: .8em;\n	position: relative;\n	top: -3px;\n	margin-left: 3px;\n}\n\nfieldset .field-instruct label, fieldset .field-instruct em {\n	margin: 0px;\n	display: block;\n	position: relative;\n}\n\nfieldset .field-instruct > label ~ em {\n	font-size: .875em;\n	color: #6c757d;\n}\n\nfieldset .fieldset-control input, fieldset .fieldset-control textarea {\n	display: block;\n	width: 100%;\n	padding: 0.375rem 0.75rem;\n	font-size: 1rem;\n	font-weight: 400;\n	line-height: 1.5;\n	color: #212529;\n	background-color: #fff;\n	background-clip: padding-box;\n	border: 1px solid #ced4da;\n	-webkit-appearance: none;\n	-moz-appearance: none;\n	appearance: none;\n	border-radius: 0.25rem;\n	transition: border-color .15s ease-in-out, box-shadow .15s ease-in-out;\n}\n\n\n/* --------------------------- \n	Custom Page Scrollbar\n	--------------------------- */\n/* width */\n::-webkit-scrollbar {\n	width: 10px;\n}\n/* Track */\n::-webkit-scrollbar-track {\n	background: #f1f1f1;\n	box-shadow: inset 0 0 5px grey;\n	border-radius: 10px;\n}\n/* Handle */\n::-webkit-scrollbar-thumb {\n	background: #888;\n	border-radius: 10px;\n}\n/* Handle on hover */\n::-webkit-scrollbar-thumb:hover {\n	background: #555;\n}\n\n\n\n.convo-messages {\n	max-height: 60vh;\n	min-height: 60vh;\n	overflow-y: auto;\n}'),
