INSERT INTO `exp_snippets` (`snippet_id`, `site_id`, `snippet_name`, `snippet_contents`, `edit_date`) VALUES 
	(1, 1, 'snp_artist_list_item', '<div class=\"d-flex flex-column flex-md-row\">\n    {if avatar}\n    <img src=\"{avatar_url}\" alt=\"{screen_name}\'s avatar\" width=\"100\"/>\n    {/if}\n    <div class=\"d-flex flex-column ms-md-3\">\n        <a class=\"fw-bold fs-4\" href=\"{path=\'artist/view/{member_id}\'}\">{if \'{first_name}\' != \'\' && \'{last_name}\' != \'\'}{first_name} {last_name}{if:else}{screen_name}{/if}</a>\n        {if last_activity}<span class=\"\">Last Activity: {last_activity:relative}</span>{/if}\n        {if {exp:art_hub:author_entry_count member_id=\"{member_id}\"} > 0}\n        	<a href=\"{path=\'artist/blog/{member_id}\'}\">{exp:art_hub:author_entry_count member_id=\"{member_id}\"} Posts</a>\n         {/if}\n        {if total_comments}\n        <a href=\"{path=\'artist/comments/{member_id}\'}\">{total_comments} Comments</a>\n      	{/if}\n    </div>\n</div>', 1638471108),
	(2, 1, 'snp_breadcrumbs', '{if layout:breadcrumbs}\n<div id=\"pageBreadcrumbsContainer\" class=\"container\">\n    <nav id=\"pageBreadcrumbs\" style=\"--bs-breadcrumb-divider: \'>\';\" aria-label=\"breadcrumb\">\n        <ol class=\"breadcrumb\">\n            <li class=\"breadcrumb-item\">\n                <a href=\"{homepage}\" class=\"text-decoration-none\">{site_name}</a>\n            </li>\n            {layout:breadcrumbs}\n            <li class=\"breadcrumb-item\"{if count != total_results} aria-current=\"page\"{/if}>\n                {if count != total_results}<a class=\"text-decoration-none\" href=\"{path=\'{layout:breadcrumbs_urls index=\'{index}\'}\'}\">{/if}{value}{if count != total_results}</a>{/if}\n            </li>\n            {/layout:breadcrumbs}\n        </ol>\n    </nav>\n</div>\n{/if}', 1638471108),
	(4, 1, 'snp_footer_navbar', '<nav class=\"navbar navbar-expand-sm navbar-dark d-none d-md-block\">\n    <div class=\"container\">\n        <ul class=\"navbar-nav me-auto mb-2 mb-lg-0\">\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"https://github.com/RK-MSU/CSCI-331-Final-Project\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"Check us out on Github\">\n                    <i class=\"fab fa-github\"></i>\n                </a>\n            </li>\n            {if logged_in && member_group == 1}\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"{cp_url}\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"{site_name} Control Panel\">\n                    <i class=\"fas fa-users-cog\"></i>\n                </a>\n            </li>\n            {/if}\n        </ul>\n        <ul class=\"navbar-nav ms-auto mb-2 mb-lg-0\">\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"https://www.cs.montana.edu/defrance/classes/331/\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"CSCI-331 Web Development\">\n                    <i class=\"fas fa-school\"></i>\n                </a>\n            </li>\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"https://expressionengine.com/\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"ExpressionEngine\">\n                    <img src=\"{site_url}images/uploads/site/ee-logo-white.svg\" class=\"ee-logo\"/>\n                </a>\n            </li>\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"https://getbootstrap.com/docs/5.1/getting-started/introduction/\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"Bootstrap v5.1\">\n                    <i class=\"fab fa-bootstrap\"></i>\n                </a>\n            </li>\n            <li class=\"nav-item\">\n                <a class=\"nav-link\" href=\"https://fontawesome.com/\" data-bs-toggle=\"tooltip\" data-bs-placement=\"top\" title=\"Fonticons, Inc.\">\n                    <i class=\"far fa-flag\"></i>\n                </a>\n            </li>\n        </ul>\n    </div>\n</nav>', 1638471108),
	(5, 1, 'snp_header_navbar', '<nav class=\"navbar navbar-expand-md navbar-custom\">\n	<div class=\"container\">\n		{!-- navbar brand --}\n		<a class=\"navbar-brand\" href=\"{site_url}\">\n			<img src=\"{site_url}icon.jpg\" class=\"rounded-3\" alt=\"{site_name} icon\" width=\"30\" height=\"24\">\n			{site_name}\n		</a>\n		{!-- navbar toggle button (when collapsed) --}\n		<button class=\"navbar-toggler\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#headerNavbarNavContent\" aria-controls=\"headerNavbarNavContent\" aria-expanded=\"false\" aria-label=\"Toggle navigation\">\n			<i class=\"fas fa-bars\"></i>\n		</button>\n		{!-- navbar nav --}\n		<div class=\"collapse navbar-collapse\" id=\"headerNavbarNavContent\">\n			<ul class=\"navbar-nav ms-auto mb-2 mb-lg-0\">\n				{!-- homepage link \n				<li class=\"nav-item\">\n					<a class=\"nav-link{if segment_1 == \'\'} active{/if}\" {if segment_1==\'\' } aria-current=\"page\" {/if} href=\"{site_url}\">Home</a>\n				</li>\n				--}\n				{!-- posts link --}\n				<li class=\"nav-item\">\n					<a class=\"nav-link{if segment_1 == \'post\'} active{/if}\" {if segment_1==\'post\' } aria-current=\"page\" {/if} href=\"{path=\'post\'}\">Posts</a>\n				</li>\n				{!-- artist link --}\n				<li class=\"nav-item\">\n					<a class=\"nav-link{if segment_1 == \'artist\'} active{/if}\" {if segment_1==\'artist\' } aria-current=\"page\" {/if} href=\"{path=\'artist\'}\">Artists</a>\n				</li>\n				{!-- contact us link --}\n				<li class=\"nav-item\">\n					<a class=\"nav-link{if segment_1 == \'home\' && segment_2 == \'contact-us\'} active{/if}\" {if segment_1==\'home\' && segment_2==\'contact-us\' } aria-current=\"page\" {/if} href=\"{path=\'home/contact-us\'}\">Contact-Us</a>\n				</li>\n				{if logged_in}\n				{!-- member dropdown --}\n				<li class=\"nav-item dropdown\">\n					<a class=\"nav-link dropdown-toggle\" href=\"#\" id=\"loggedInUserHeaderNavbarDropdownNavItem\" role=\"button\" data-bs-toggle=\"dropdown\" aria-expanded=\"false\">{username}</a>\n					<ul class=\"dropdown-menu dropdown-menu-end\" aria-labelledby=\"loggedInUserHeaderNavbarDropdownNavItem\">\n						<li><h6 class=\"dropdown-header\">Manage Content</h6></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'messages\'}\"><i class=\"fas fa-inbox\"></i> Messages</a></li>\n						\n						<li><a class=\"dropdown-item\" href=\"{path=\'requests\'}\"><i class=\"fa-solid fa-handshake\"></i> Requests</a></li>\n						{if member_group == 1 || member_group == 6}\n						<li><hr class=\"dropdown-divider\"></li>\n						<li><h6 class=\"dropdown-header\">For Artists</h6></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'commissions/list/{member_id}\'}\"><i class=\"fas fa-file-contract\"></i> Commissions</a></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'artist/blog/{member_id}\'}\"><i class=\"fas fa-th-list\"></i> Blog Posts</a></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'post/edit/new\'}\"><i class=\"far fa-edit\"></i> New Post</a></li>\n						<li><hr class=\"dropdown-divider\"></li>\n						<li><h6 class=\"dropdown-header\">User Action</h6></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'artist/view/{logged_in_member_id}\'}\"><i class=\"fas fa-user\"></i> Profile</a></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'artist/settings\'}\"><i class=\"fas fa-cogs\"></i> Settings</a></li>\n						{/if}\n						{if member_group == 1}{!-- only super admins --}\n						<li><a class=\"dropdown-item\" href=\"{cp_url}\" target=\"_blank\"><i class=\"fas fa-sliders-h\"></i> Control Panel</a></li>\n						{/if}\n						<li><hr class=\"dropdown-divider\"></li>\n						<li><a class=\"dropdown-item\" href=\"{path=\'logout\'}\"><i class=\"fas fa-sign-out-alt\"></i> Log Out</a></li>\n					</ul>\n				</li>\n				{if:else}\n				{!-- log-in link --}\n				<li class=\"nav-item\">\n					<a class=\"nav-link\" href=\"{path=\'home/login\'}\">Log-in</a>\n				</li>\n				{/if}\n			</ul>\n		</div>\n	</div>\n</nav>', 1638500709),
	(6, 1, 'snp_pagination', '{paginate}\n<div class=\"d-flex justify-content-start\">\n  <div class=\"align-self-center\">\n    <nav aria-label=\"Page navigation example\">\n      <ul class=\"pagination\">\n      {pagination_links always_show_first_last=\"yes\"}\n          {first_page}\n          {if current_page != 1}\n          <li class=\"page-item{if current_page == 1} disabled{/if}\">\n              <a class=\"page-link\" href=\"{pagination_url}\" aria-label=\"First\">\n                <span>&laquo;</span>\n              </a>\n          </li>\n          {/if}\n          {/first_page}\n          {previous_page}\n          <li class=\"page-item\">\n              <a class=\"page-link\" href=\"{pagination_url}\" aria-label=\"Previous\">\n                  <span>Previous</span>\n              </a>\n          </li>\n          {/previous_page}\n          {page}\n          <li class=\"page-item page-{pagination_page_number} {if current_page}active{/if}\">\n              <a class=\"page-link\" href=\"{pagination_url}\">{pagination_page_number}</a>\n          </li>\n          {/page}\n          {next_page}\n          <li class=\"page-item\">\n              <a class=\"page-link\" href=\"{pagination_url}\" aria-label=\"Next\">\n                  <span>Next</span>\n              </a>\n          </li>\n          {/next_page}\n          {last_page}\n          {if current_page != total_pages}\n          <li class=\"page-item{if current_page == total_pages} disabled{/if}\">\n              <a class=\"page-link\" href=\"{pagination_url}\" aria-label=\"Last\">\n                  <span>&raquo;</span>\n              </a>\n          </li>\n          {/if}\n          {/last_page}\n      {/pagination_links}\n      </ul>\n  </nav>\n    \n  </div>\n\n</div>\n\n{!-- <p>Page {current_page} of {total_pages}</p> --}\n{/paginate}', 1638471108),
	(7, 1, 'snp_post_list_item', '<div class=\"card mb-2 clickable post\" data-nav-href=\"{path=\'post/view/{url_title}\'}\">\n    <div class=\"card-header d-flex flex-row justify-content-between\">\n		<div class=\"flex-fill\">\n            {embed=\"common/_member-info\" member_id=\"{author_id}\" avatar=\"yes\" last_activity=\'no\' total_entries=\'no\' }\n        </div>\n        {if logged_in && (logged_in_member_group == 1 || logged_in_member_id == author_id)}\n        <div class=\"d-flex flex-row\">\n            <a class=\"btn btn-sm btn-outline-secondary\" href=\"{path=\'post/edit/{entry_id}\'}\" data-bs-toggle=\"tooltip\" data-bs-placement=\"bottom\" title=\"Edit Post\"><i class=\"fas fa-edit\"></i></a>\n            {if logged_in_member_group == 1}<a class=\"btn btn-sm btn-outline-secondary ms-1\" href=\"{cp_edit_entry_url}\"  data-bs-toggle=\"tooltip\" data-bs-placement=\"bottom\" title=\"View in Control Panel\"><i class=\"fas fa-cogs\"></i></a>{/if}\n        </div>\n        {/if}\n	</div>\n    <div class=\"card-body\">\n        <div class=\"d-flex flex-column flex-lg-row\">\n            {if art_img}\n            <img src=\"{art_img}\" class=\"align-self-center align-self-lg-start\" alt=\"...\" width=\"200\">\n            {/if}\n            <div class=\"flex-fill d-flex flex-column ms-md-3\">\n                <div class=\"d-flex flex-column flex-md-row justify-content-md-between\">\n                    <h5 class=\"card-title fs-4 mb-0\">\n                        <a class=\"text-decoration-none\" href=\"{path=\'post/view/{url_title}\'}\">{title}</a>\n                    </h5>\n                    <span class=\"small text-muted align-self-md-center text-nowrap\">{art_date:relative}</span>\n                </div>\n                {!-- categories --}\n                {if has_categories}\n                <span class=\"small post-categories py-1\">\n                    {snp_post_categories}\n                </span>\n                {/if}\n                <p class=\"small flex-fill\">{art_desc:limit characters=\"400\"}</p>\n                <a href=\"{path=\'post/comments/{url_title}\'}\" class=\"align-self-end small fst-italic text-decoration-none\">{comment_total} Comment{if comment_total > 1 || comment_total == 0}s{/if}</a>\n            </div>\n        </div>\n    </div>\n</div>', 1638471108),
	(8, 1, 'snp_post_card_item', '<div class=\"card shadow-sm clickable post\" data-nav-href=\"{path=\'post/view/{url_title}\'}\">\n	{!-- card header (author tagline) --}\n	{if \"{show_member_post_header}\" == \'yes\'}\n	<div class=\"card-header d-flex flex-row justify-content-between\">\n		<div class=\"flex-fill\">\n            {embed=\"common/_member-info\" member_id=\"{author_id}\" avatar=\"yes\" last_activity=\'no\' total_entries=\'no\' }\n        </div>\n		{if logged_in && logged_in_member_group == 1}\n        <div class=\"d-flex flex-row\">\n            <a class=\"btn btn-sm btn-outline-secondary ms-1 p-1\" href=\"{cp_edit_entry_url}\" data-bs-toggle=\"tooltip\" data-bs-placement=\"bottom\" title=\"View in Control Panel\"><i class=\"fas fa-sm fa-cogs\"></i></a>\n        </div>\n        {/if}\n	</div>\n	{/if}\n\n	{!-- post image --}\n	{if art_img}\n	<img src=\"{art_img}\" class=\"card-img-top post-art\" alt=\"Art Post: {art_img}{title}{/art_img}\">\n	{/if}\n\n	{!-- card body content --}\n	<ul class=\"list-group list-group-flush art-post-content\">\n		\n		{!-- post title and date --}\n		<li class=\"list-group-item d-flex flex-row flex-wrap post-title\">\n			<span class=\"flex-fill fw-bold m-0 fs-6 lh-1\">{title}</span>\n			<span class=\"small fst-italic align-self-center lh-1 text-muted\">{art_date:relative}</span>\n		</li>\n\n		{!-- post description --}\n		{if art_desc}\n		<li class=\"list-group-item post-desc\">\n			<p class=\"card-text small lh-1\">{art_desc:limit characters=\"150\"}</p>\n		</li>\n		{/if}\n\n		{!-- categories --}\n		{if has_categories}\n		<li class=\"list-group-item small post-categories\">\n			{snp_post_categories}\n		</li>\n		{/if}\n	</ul>\n</div>', 1638471108),
	(9, 1, 'snp_legacy_member_dropdown', '<li class=\"nav-item dropdown\">\n	<a class=\"nav-link dropdown-toggle\" href=\"#\" id=\"legacyMemberNavbarDropdownNavItem\" role=\"button\" data-bs-toggle=\"dropdown\" aria-expanded=\"false\">Legacy Templates</a>\n	<ul class=\"dropdown-menu dropdown-menu-end\" aria-labelledby=\"legacyMemberNavbarDropdownNavItem\">\n		{if member_group == 1}\n		<li><a class=\"dropdown-item\" href=\"{cp_url}?/cp/design/members\" target=\"_blank\">Edit Legacy Templates</a></li>\n		<li><hr class=\"dropdown-divider\"></li>\n		{/if}\n		<li><h6 class=\"dropdown-header\">General</h6></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/profile\'}\">Your Control Panel</a></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/memberlist\'}\">View the Memberlist</a></li>\n		<li><hr class=\"dropdown-divider\"></li>\n		<li><h6 class=\"dropdown-header\">Profile</h6></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/edit_profile\'}\">Edit Profile</a></li>\n		<li><hr class=\"dropdown-divider\"></li>\n		<li><h6 class=\"dropdown-header\">Messages</h6></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/messages/compose\'}\">New Message</a></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/messages/view_folder/1/\'}\">Inbox</a></li>\n		<li><a class=\"dropdown-item\" href=\"{path=\'member/messages/view_folder/2/\'}\">Sent</a></li>\n	</ul>\n</li>', 1638471108),
	(11, 1, 'snp_artist_comment', '<div class=\"card shadow-sm comment artist-comment\">\n    <div class=\"card-header text-muted\">{comment_date:relative}</div>\n    <div class=\"card-body bg-light py-1 my-0 d-flex flex-column flex-md-row justify-content-md-between\">\n        <div class=\"pe-md-2\">{comment}</div>\n        <div class=\"align-self-start d-flex flex-column\">\n            <span class=\"text-muted small\">Post:</span>\n            {exp:channel:entries dynamic=\"no\" limit=\"1\" entry_id=\"{entry_id}\"}\n            <div class=\"d-flex flex-row text-nowrap post-preview w-25\">\n                <img src=\"{art_img}\" alt=\"{art_img}{title}{/art_img}\" class=\"me-1 rounded border border-2 border-secondary align-self-center\" height=\"35\" width=\"35\">\n                <div class=\"align-self-center d-flex flex-row flex-md-column justify-content-md-between\">\n                    <a href=\"{path=\'post/view/{url_title}\'}\" class=\"fs-6 fw-bold text-decoration-none\">{title}</a>\n                    <span class=\"align-self-end align-self-md-start ms-1 ms-md-0\">by <a href=\"{path=\'artist/view/{author_id}\'}\" class=\"text-decoration-none\">{embed=\"common/_member-name\" member_id=\"{author_id}\"}</a></span>\n                </div>\n            </div>\n            {/exp:channel:entries}\n        </div>\n    </div>\n</div>', 1638471108),
	(12, 1, 'snp_comment_form', '{if logged_out}\n<fieldset class=\"fieldset-required\">\n    <div class=\"field-instruct\">\n        <label for=\"name\">Name</label>\n    </div>\n    <div class=\"fieldset-control\">\n        <input type=\"text\" name=\"name\" id=\"name\" value=\"{name}\" required>\n    </div>\n</fieldset>\n<fieldset class=\"fieldset-required\">\n    <div class=\"field-instruct\">\n        <label for=\"email\">email</label>\n    </div>\n    <div class=\"fieldset-control\">\n        <input type=\"text\" name=\"email\" id=\"email\" value=\"{email}\" required>\n    </div>\n</fieldset>\n<fieldset class=\"fieldset-required\">\n    <div class=\"field-instruct\">\n        <label for=\"location\">Location</label>\n    </div>\n    <div class=\"fieldset-control\">\n        <input type=\"text\" name=\"location\" id=\"location\" value=\"{location}\" required>\n    </div>\n</fieldset>\n<fieldset class=\"fieldset-required\">\n    <div class=\"field-instruct\">\n        <label for=\"url\">URL</label>\n    </div>\n    <div class=\"fieldset-control\">\n        <input type=\"text\" name=\"url\" id=\"url\" value=\"{url}\" required>\n    </div>\n</fieldset>\n{/if}\n<fieldset class=\"fieldset-required\">\n    <div class=\"field-instruct\">\n        <label for=\"comment\">Comment</label>\n    </div>\n    <div class=\"fieldset-control\">\n        <textarea name=\"comment\" id=\"comment\" cols=\"5\" rows=\"5\" class=\"form-control\" placeholder=\"Write your comment here...\" maxlength=\"5000\" required>{comment}</textarea>\n    </div>\n</fieldset>\n{if logged_out}\n<div class=\"form-check mb-3\">\n    <input class=\"form-check-input\" type=\"checkbox\" value=\"yes\" name=\"save_info\" id=\"save_info\" {save_info}>\n    <label class=\"form-check-label\" for=\"save_info\">Remember my personal information</label>\n</div>\n{/if}\n<div class=\"form-check mb-3\">\n    <input class=\"form-check-input\" type=\"checkbox\" value=\"yes\" name=\"notify_me\" id=\"notify_me\" {notify_me}>\