	(11, 1, 2, '_member-tagline', 'webpage', '{!-- use in combination with \"common/_member-info\" --}\n{preload_replace:def_avatar_size=\"25\"}\n\n<div class=\"d-flex flex-column\">\n    <div class=\"d-flex flex-row justify-content-start\">\n        <img src=\"{layout:avatar}\" class=\"me-1 rounded-circle border border-2 border-secondary align-self-center\" height=\"{if layout:avatar_size}{layout:avatar_size}{if:else}{def_avatar_size}{/if}\" width=\"{if layout:avatar_size}{layout:avatar_size}{if:else}{def_avatar_size}{/if}\">\n        {if layout:link}<a href=\"{layout:link}\" class=\"text-decoration-none align-self-center\">{/if}\n            <span class=\"align-self-center fs-6 fw-bold\">{layout:member_name}</span>\n        {if layout:link}</a>{/if}\n    </div>\n    {if layout:last_activity}\n    <div class=\"align-self-start small\">\n        <span class=\"align-self-center small text-muted\">Last Active: {layout:last_activity}</span>\n    </div>\n    {/if}\n    {if layout:total_entries}\n    <div class=\"align-self-start small\">\n        <span class=\"align-self-center small text-muted\">Posts: {layout:total_entries}</span>\n    </div>\n    {/if}\n</div>\n', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(12, 1, 2, '_post-layout', 'webpage', '{layout=\'layouts/_html-wrapper\'}\n\n{layout:set:prepend name=\'breadcrumbs\'}Posts{/layout:set:prepend}\n{layout:set:prepend name=\'breadcrumbs_urls\'}post{/layout:set:prepend}\n\n{layout:contents}\n\n{if layout:allow_comments}\n<div class=\"mt-3\">\n    <h4>Comments</h4>\n    {if layout:comment_total > 0}\n        <div class=\"d-flex flex-column\">\n            {exp:comment:entries sort=\"desc\" orderby=\"date\" limit=\"5\" url_title=\"{layout:url_title}\"}\n            {snp_post_comment}\n            {/exp:comment:entries}\n        </div>\n        <a class=\"btn btn-primary btn-sm\" href=\"{path=\'post/comments/{segment_3}\'}\">View All Comments</a>\n    {if:else}\n        <div class=\"alert alert-secondary\" role=\"alert\">\n            No Comments\n        </div>\n    {/if}\n    {exp:comment:form channel=\"post\" return=\"\"}\n    <div class=\"card mt-4\">\n        <div class=\"card-header\">\n            Leave Comment\n        </div>\n        <div class=\"card-body\">\n            {snp_comment_form}\n        </div>\n        <div class=\"card-footer\">\n            <button type=\"submit\" class=\"btn btn-sm btn-primary\">Make Comment</button>\n        </div>\n    </div>\n    {/exp:comment:form}\n</div>\n{/if}', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(13, 1, 2, '_post-list-layout', 'webpage', '{layout=\'layouts/_html-wrapper\'}\n\n{layout:set name=\'title\'}{layout:title}{/layout:set}\n\n{if layout:cat_id}\n    {layout:set name=\"title\"}{layout:category} Posts{/layout:set}\n    {layout:set:append name=\'breadcrumbs\'}{layout:category}{/layout:set:append}\n    {layout:set:append name=\'breadcrumbs_urls\'}{p_url}/category/{layout:cat_id}{/layout:set:append}\n{/if}\n\n<div class=\"d-flex flex-column flex-lg-row-reverse\">\n    <div class=\"sidebar-toc\">\n        <span class=\"fw-bold fs-5\">Post Categories</span>\n        <button class=\"navbar-toggler\" type=\"button\" data-bs-toggle=\"collapse\" data-bs-target=\"#categoriesSidebar\" aria-expanded=\"true\" aria-controls=\"categoriesSidebar\">\n            <i class=\"fas fa-bars\"></i>\n        </button>\n        <div class=\"collapse\" id=\"categoriesSidebar\">\n            {exp:channel:categories channel=\'{layout:ch}\' show_empty=\"no\" channel=\"post\" style=\"nested\"}\n            <a href=\"{path=\'{layout:p_url}/category/{category_id}\'}\" class=\"{if layout:cat_id == category_id}active{/if}\">{category_name}</a>\n            {/exp:channel:categories}\n        </div>\n    </div>\n    <div class=\"flex-fill\">\n        {layout:contents}\n    </div>\n</div>', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(14, 1, 3, '_global-css', 'css', '/* \n--------------------------- \n	Global CSS\n--------------------------- \n*/\n\n:root {\n	--art_hub-green: #4c947e;\n	--art_hub-blue: #24314d;\n}\n\nbody {\n	/* background-color: #24314d; */\n	background-color: rgba(0,0,0,0.1);\n	/* color: #e3dbdc; */\n	/* padding to avoid content beneath fixed header navbar */\n	padding-top: 70px;\n	/* padding to avoid content beneath fixed footer navbar */\n	padding-bottom: 70px; \n}\n\n\n\n\n/* --------------------------- \n	Navbars\n--------------------------- */\n/* header nvabar */\nheader > nav {\n	background-color: var(--art_hub-green);\n}\n/* navbar-brand & navbar links color */\nheader > nav .navbar-brand, \nheader > nav .navbar-nav > .nav-item > .nav-link {\n	color: midnightblue;\n}\n/* active link color */\nheader > nav .navbar-nav > .nav-item > .nav-link.active {\n	color: black;\n}\nheader > nav .navbar-nav > .nav-item > .nav-link {\n	border-radius: 10px;\n}\nheader > nav .navbar-nav > .nav-item:hover .nav-link {\n	color: #e3dbdc;\n	box-shadow: #24314d 1px 1px 3px;\n	background-color: seagreen;\n}\nheader > nav .navbar-toggler {\n	background-color: #4c947e;\n	box-shadow: #24314d 2px 2px 5px;\n}\nheader > nav .navbar-toggler i {\n	color: var(--art_hub-green);\n}\n\n/* footer navbar */\nfooter > nav {\n	background-color: var(--art_hub-blue);\n}\n\n/* --------------------------- \n	Navbar Positioning\n	- fixed header nav to top\n	- fix footer nav to bottom\n--------------------------- */\nfooter, header{\n	z-index:10;\n	position: fixed;\n	left: 0;\n	right: 0;\n}\nheader {\n	top: 0;\n	/* box-shadow: 0 4px 8px 0 rgba(0, 0, 0, .2), 0 6px 20px 0 rgba(0, 0, 0, .19) */\n}\nfooter {\n	bottom: 0;\n	/* box-shadow: 0 -4px 8px 0 rgba(0, 0, 0, .2), 0 -6px 20px 0 rgba(0, 0, 0, .19) */\n}\n\nimg.ee-logo{\n	height:16px;\n	padding-bottom:1px\n}\n\n/* --------------------------- \n	Category Sidebar\n	--------------------------- */\nul.category-list {\n	list-style: none;\n}\n\nul.category-list li {\n	display: inline;\n}\n\n.category-label {\n	border: 1px solid grey;\n}\n\n\n/* --------------------------- \n	Custom Page Scrollbar\n	--------------------------- */\n/* width */\n::-webkit-scrollbar {\n	width: 10px;\n}\n/* Track */\n::-webkit-scrollbar-track {\n	background: #f1f1f1;\n	box-shadow: inset 0 0 5px grey;\n	border-radius: 10px;\n}\n/* Handle */\n::-webkit-scrollbar-thumb {\n	background: #888;\n	border-radius: 10px;\n}\n/* Handle on hover */\n::-webkit-scrollbar-thumb:hover {\n	background: #555;\n}\n\n.convo-messages {\n	max-height: 60vh;\n	min-height: 60vh;\n	overflow-y: auto;\n}', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
	(15, 1, 3, '_member-info', 'webpage', '{!-- use this template to get a members name --}\n{layout=\"layouts/_member-tagline\"}\n\n{if embed:avatar_size}\n    {layout:set name=\"avatar_size\" value=\"{embed:avatar_size}\"}\n{/if}\n\n{exp:member:custom_profile_data member_id=\"{embed:member_id}\"}\n\n    {if embed:hyperlink != \'no\'}\n    {layout:set name=\"link\"}{path=\"artist/view/{member_id}\"}{/layout:set}\n    {/if}\n    \n    {!-- Avatar --}\n\n    {if embed:avatar == \'yes\' && avatar}\n        {layout:set name=\"avatar\"}{avatar_url}{/layout:set}\n    {/if}\n\n    {!-- Name --}\n    {layout:set name=\"member_name\"}{embed=\"common/_member-name\" member_id=\"{embed:member_id}\"}{/layout:set}\n\n    {!-- Last Activity --}\n\n    {if embed:last_activity == \'yes\'}\n        {layout:set name=\"last_activity\"}{last_activity:relative}{/layout:set}\n    {/if}\n\n    {!-- Total Entries --}\n\n    {if embed:total_entries == \'yes\'}\n        {layout:set name=\"total_entries\"}{total_entries}{/layout:set}\n    {/if}\n\n{/exp:member:custom_profile_data}', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 0, 'n', 'y'),
