	(17, 1, 1, 'contact-us', 'webpage', '{layout=\"layouts/_html-wrapper\"}\n{if segment_3 != \'\' && segment_3 != \'thanks\'}\n	{redirect=\'home/contact-us\'}\n{/if}\n\n{if segment_3 == \'thanks\'}\n	{layout:set name=\"title\"}Thanks!{/layout:set}\n	<div class=\"alert alert-success\" role=\"alert\">\n  		We received you submission. We will get back to you as soon as possible!\n	</div>\n{if:else}\n	{layout:set name=\"title\"}Contact-Us{/layout:set}\n	<div class=\"row\">\n		<div class=\"col-12 col-md-6 m-auto\">\n					{exp:channel:form \n						channel=\"contact_us\" \n						datepicker=\"no\" \n						include_assets=\"no\" \n						include_css=\"no\"\n						include_jquery=\"no\"\n						logged_out_member_id=\"1\"\n						require_entry=\"no\"\n						return=\"home/contact-us/thanks\"\n						site=\"csci331\"\n						show_fields=\"contact_us_name|contact_us_email|contact_us_message\"\n						unique_url_title=\"yes\"\n						dynamic_title=\"[contact_us_name] - [contact_us_email]\"}\n			<div class=\"card shadow\">\n				<div class=\"card-body\">\n					{!-- Name --}\n					<fieldset class=\"fieldset-required\">\n						<div class=\"field-instruct\">\n							<label for=\"name\">{label:contact_us_name}</label>\n						</div>\n						<div class=\"fieldset-control\">\n							<input type=\"text\" name=\"contact_us_name\" id=\"name\" value=\"{contact_us_name}\" maxlength=\"100\" required>\n							<div class=\"form-text\">{instructions:contact_us_name}</div>\n						</div>\n					</fieldset>\n\n					{!-- email --}\n					<fieldset class=\"fieldset-required\">\n						<div class=\"field-instruct\">\n							<label for=\"email\">{label:contact_us_email}</label>\n						</div>\n						<div class=\"fieldset-control\">\n							<input type=\"text\" name=\"contact_us_email\" id=\"email\" value=\"{contact_us_email}\" maxlength=\"100\" required>\n							<div class=\"form-text\">{instructions:contact_us_email}</div>\n\n						</div>\n					</fieldset>\n\n\n					{!-- message --}\n					<fieldset class=\"fieldset-requ