	(18, 1, 1, 'forgot-password', 'webpage', '{layout=\"layouts/_html-wrapper\"}\n\n{!-- redirect logged in members --}\n{if logged_in}{redirect=\'{site_url}\'}{/if}\n\n{layout:set name=\'title\' value=\'Forgot Password\'}\n\n<div class=\"row\">\n    <div class=\"col col-md-8 col-lg-6 m-auto\">\n        <div class=\"card shadow-lg\">\n            <div class=\"card-body\">\n                {exp:member:forgot_password_form return=\"home/login/forgot-password\"}\n				<div class=\"mb-3\">\n					<label for=\"emailAddressInput\" class=\"form-label\">Email Address</label>\n					<input type=\"email\" class=\"form-control\" id=\"emailAddressInput\" name=\"email\" value=\"\" maxlength=\"120\" autocomplete=\"email\" required>\n				</div>\n				<button type=\"submit\" class=\"btn btn-primary\">Submit</button>\n				{/exp:member:forgot_password_form}\n            </div>\n            <div class=\"card-footer\">\n                Forgot username? <a href=\"{path=\'home/forgot-username\'}\">Get Username</a>\n            </div>\n        </div>\n    </div>\n</div>', '', 1638471088, 1, 'n', 0, '', 'n', 'n', 'o', 1, 'n', 'y'),
