	(20, 1, 1, 'login', 'webpage', '{layout=\'layouts/_html-wrapper\'}\n\n{!-- redirect logged in members --}\n{if logged_in}{redirect=\'{site_url}\'}{/if}\n\n{layout:set name=\'title\' value=\'Login\'}\n\n{if segment_3 == \'forgot-username\'}\n<div class=\"alert alert-success\" role=\"alert\">\n        We sent you an email with your <strong>username</strong>, Check your mailbox!\n</div>\n{if:elseif segment_3 == \'forgot-password\'}\n<div class=\"alert alert-success\" role=\"alert\">\n    We sent you an email to reset your <strong>username</strong>, Check your mailbox!\n</div>\n{/if}\n\n<div class=\"row\">\n    <div class=\"col col-md-8 col-lg-6 m-auto\">\n        <div class=\"card shadow-lg\">\n            <div class=\"list-group list-group-flush\">\n                <div class=\"list-group-item\">\n                    {exp:member:login_form}\n                    <div class=\"mb-3\">\n                        <label for=\"usernameLoginInput\" class=\"form-label\">Username</label>\n                        <input type=\"text\" class=\"form-control\" id=\"usernameLoginInput\" name=\"username\" value=\"\" maxlength=\"32\" placeholder=\"Username\" autocomplete=\"username\" aria-describedby=\"usernameLoginHelp\">\n                        <div id=\"usernameLoginHelp\" class=\"form-text\">Enter you username.</div>\n                    </div>\n                    <div class=\"mb-3\">\n                        <label for=\"passwordLoginInput\" class=\"form-label\">Password</label>\n                        <input type=\"password\" class=\"form-control\" id=\"passwordLoginInput\" name=\"password\" value=\"\" maxlength=\"32\" placeholder=\"Password\" autocomplete=\"current-password\" aria-describedby=\"passwordLoginHelp\">\n                        <div id=\"passwordLoginHelp\" class=\"form-text\">Enter your password.</div>\n                    </div>\n                    <button type=\"submit\" class=\"btn btn-primary\">Login</button>\n                    {/exp:member:login_form}\n  